DROP TABLE FLIGHTS IF EXISTS
DROP TABLE CITIES IF EXISTS
CREATE TABLE CITIES (CITY_ID INTEGER NOT NULL, CITY_NAME VARCHAR(24) NOT NULL, COUNTRY VARCHAR(26) NOT NULL, AIRPORT VARCHAR(3), LANGUAGE  VARCHAR(16), COUNTRY_ISO_CODE CHAR(2));
CREATE TABLE FLIGHTS (FLIGHT_ID CHAR(6) NOT NULL, SEGMENT_NUMBER INTEGER NOT NULL, ORIG_AIRPORT CHAR(3), DEPART_TIME TIME, DEST_AIRPORT CHAR(3), ARRIVE_TIME TIME, MEAL CHAR(1), FLYING_TIME DOUBLE PRECISION, MILES INTEGER, AIRCRAFT VARCHAR(6));
ALTER TABLE CITIES ADD CONSTRAINT CITIES_PK Primary Key (CITY_ID);
ALTER TABLE FLIGHTS ADD CONSTRAINT FLIGHTS_PK Primary Key (FLIGHT_ID, SEGMENT_NUMBER);